CREATE TABLE [dbo].[Scenario] (
    [Id]       INT            IDENTITY (1, 1) NOT NULL,
    [Label]    NVARCHAR (30)  NULL,
    [Text]     NVARCHAR (250) NULL,
    [GameOver] TINYINT        NOT NULL,
    PRIMARY KEY CLUSTERED ([Id] ASC)
);

CREATE TABLE [dbo].[Choice] (
    [Id]                INT            IDENTITY (1, 1) NOT NULL,
    [CurrentScenarioId] INT            NULL,
    [NextScenarioId]    INT            NOT NULL,
    [Text]              NVARCHAR (250) NOT NULL,
    PRIMARY KEY CLUSTERED ([Id] ASC),
    CONSTRAINT [FK_Choice_NextScenario] FOREIGN KEY ([NextScenarioId]) REFERENCES [dbo].[Scenario] ([Id]),
    CONSTRAINT [FK_Choice_CurrentScenario] FOREIGN KEY ([CurrentScenarioId]) REFERENCES [dbo].[Scenario] ([Id])
);

SET IDENTITY_INSERT [dbo].[Scenario] ON
SET IDENTITY_INSERT [dbo].[Scenario] OFF


SET IDENTITY_INSERT [dbo].[Choice] ON
SET IDENTITY_INSERT [dbo].[Choice] OFF
