INSERT INTO [dbo].[Scenario] ([Id], [Label], [Text], [GameOver]) VALUES (1, N'Début de l''aventure', N'Vous êtes devant la porte du donjon. Votre mission : capturer un mangemort dans le donjon, vous êtes muni de votre baguette et d''une dague pour accomplir cette tâche', 0)
INSERT INTO [dbo].[Scenario] ([Id], [Label], [Text], [GameOver]) VALUES (2, N'Lâche', N'Game Over. L''aventure fut courte, mais au moins vous êtes en vie', 2)
INSERT INTO [dbo].[Scenario] ([Id], [Label], [Text], [GameOver]) VALUES (3, N'Porte Boss', N'Vous entrez dans le donjon, une porte menaçante se trouve sur votre droite.', 0)
INSERT INTO [dbo].[Scenario] ([Id], [Label], [Text], [GameOver]) VALUES (4, N'Boss Surprise', N'Devant vous ce tien le mangemort que vous devez capturer. Cependant, il ne vous regarde pas encore, il pense sûrement que vous êtes un serviteur.', 0)
INSERT INTO [dbo].[Scenario] ([Id], [Label], [Text], [GameOver]) VALUES (5, N'Victoire Facile', N'Le mangemort est vaincu, mais à quel prix ? Vous êtes arrêté par le ministère de la magie et finirez vos jours en prison. ', 2)
INSERT INTO [dbo].[Scenario] ([Id], [Label], [Text], [GameOver]) VALUES (6, N'Defaite Prison', N'Vous frappez le mangemort derrière la tête avec une flasque qui traînait. Le mangemort est immobilisé. Vous le capturez, vous avez accompli votre mission.', 1)
INSERT INTO [dbo].[Scenario] ([Id], [Label], [Text], [GameOver]) VALUES (7, N'Intersection Départ', N'Vous arrivez à une intersection. Que faire ?', 0)
INSERT INTO [dbo].[Scenario] ([Id], [Label], [Text], [GameOver]) VALUES (8, N'Trape à pique', N'Un piège était présent et vous êtes tombé en plein dedans RIP.', 2)
INSERT INTO [dbo].[Scenario] ([Id], [Label], [Text], [GameOver]) VALUES (9, N'Porte Araignée', N'Vous vous trouvez devant une porte verrouillée.', 0)
INSERT INTO [dbo].[Scenario] ([Id], [Label], [Text], [GameOver]) VALUES (10, N'Araignée Géante', N'Vous entrez dans la salle une araignée géante fonce vers vous.', 0)
INSERT INTO [dbo].[Scenario] ([Id], [Label], [Text], [GameOver]) VALUES (11, N'Départ raté', N'L''araignée géante vous rattrape et vous dévore, vous êtes mort.', 2)
INSERT INTO [dbo].[Scenario] ([Id], [Label], [Text], [GameOver]) VALUES (12, N'A l''attaque de l''araignée', N'Vous décidez de vous battre contre l''araignée, elle n''est pas si géante finalement, quel sort lancer pour vaincre l''araignée.', 0)
INSERT INTO [dbo].[Scenario] ([Id], [Label], [Text], [GameOver]) VALUES (13, N'Passage secret', N'En faisant demi-tour vous apercevez le long du couloir un escalier caché dans le noir', 0)
INSERT INTO [dbo].[Scenario] ([Id], [Label], [Text], [GameOver]) VALUES (14, N'Elfe de Maison aggressif', N'Vous montez l''escalier. En haut de l''escalier une petite pièce éclairée, un elfe de maison est en train de travailler, il vous repère et vous fonce dessus.', 0)
INSERT INTO [dbo].[Scenario] ([Id], [Label], [Text], [GameOver]) VALUES (15, N'Victoire contre Araignée', N'L''araignée s''écroule à terre de douleur, vous l''achevez avec votre dague, c''était facile.', 0)
INSERT INTO [dbo].[Scenario] ([Id], [Label], [Text], [GameOver]) VALUES (16, N'Inefficace', N'Le sort n''a aucun effet vous êtes mort.', 2)
INSERT INTO [dbo].[Scenario] ([Id], [Label], [Text], [GameOver]) VALUES (17, N'Victoire Difficile', N'Vous arrivez à battre l''araignée tant bien que mal, vous devriez sûrement vous reposez. ', 0)
INSERT INTO [dbo].[Scenario] ([Id], [Label], [Text], [GameOver]) VALUES (18, N'Epuisement', N'Vous vous écroulez par terre, quand on vous dit de vous reposez ! Vous ne vous releverez plus jamais', 0)
INSERT INTO [dbo].[Scenario] ([Id], [Label], [Text], [GameOver]) VALUES (19, N'Mort à la traitre', N'Vous vous prenez dix flèche dans le dos, vous entendez "ARRÊTEZ DE FAIRE DEMI-TOUR", sûrement le scénariste qui ne vous aime pas, vous êtes mort', 2)
INSERT INTO [dbo].[Scenario] ([Id], [Label], [Text], [GameOver]) VALUES (20, N'Super Efficace', N'Comme tout le monde le sait les dagues sont super efficaces contre les elfes de maison. Vous observez la pièce autour de vous, une armoire, une table et un couloir pour continuer votre aventure', 0)
INSERT INTO [dbo].[Scenario] ([Id], [Label], [Text], [GameOver]) VALUES (21, N'Desarmé', N'L''elfe vous saute dessus et vous enlève la baguette des mains.', 0)
INSERT INTO [dbo].[Scenario] ([Id], [Label], [Text], [GameOver]) VALUES (22, N'Chute', N'Dans la précipitation vous tombez dans les escaliers et mourrez de votre chute.', 2)
INSERT INTO [dbo].[Scenario] ([Id], [Label], [Text], [GameOver]) VALUES (23, N'Carte', N'Vous trouvez une plan du donjon. Deux lieux attires votre attention la salle du maitre qui était la porte à l''entré et la salle des artefacts', 0)
INSERT INTO [dbo].[Scenario] ([Id], [Label], [Text], [GameOver]) VALUES (24, N'Rien', N'Vous ne trouvez rien dans l''armoire à première vue', 0)
INSERT INTO [dbo].[Scenario] ([Id], [Label], [Text], [GameOver]) VALUES (25, N'Intersection Etage', N'Vous arrivez devant une intersection', 0)
INSERT INTO [dbo].[Scenario] ([Id], [Label], [Text], [GameOver]) VALUES (26, N'Boss', N'Vous entrez dans la salle du maitre, devant vous le mangemort que vous devez capturez', 0)
INSERT INTO [dbo].[Scenario] ([Id], [Label], [Text], [GameOver]) VALUES (27, N'Mort Debile', N'Le mangemort vous tue avant que vous arriviez au corps à corps', 2)
INSERT INTO [dbo].[Scenario] ([Id], [Label], [Text], [GameOver]) VALUES (28, N'Repli Strategique', N'Vous faites demi-tour et ressortez, vous entendez le mangemort se ruez sur vous', 0)
INSERT INTO [dbo].[Scenario] ([Id], [Label], [Text], [GameOver]) VALUES (29, N'Inefficace', N'Votre sort est facilement contré', 0)
INSERT INTO [dbo].[Scenario] ([Id], [Label], [Text], [GameOver]) VALUES (30, N'Pourparler', N'Le mangemort à l''air ouvert à la discussion.', 0)
INSERT INTO [dbo].[Scenario] ([Id], [Label], [Text], [GameOver]) VALUES (31, N'Efficace', N'Vous coupez la main du mangemort aussi tôt qu''il sort, il est à terre', 0)
INSERT INTO [dbo].[Scenario] ([Id], [Label], [Text], [GameOver]) VALUES (32, N'Felicitations', N'Vous petrifiez le mangemort, votre mission est accompli. Bravo', 1)
INSERT INTO [dbo].[Scenario] ([Id], [Label], [Text], [GameOver]) VALUES (33, N'Lisez les consignes', N'Vous avz tuez le mangemort... Votre mission était de le capturez.', 2)
INSERT INTO [dbo].[Scenario] ([Id], [Label], [Text], [GameOver]) VALUES (34, N'Pas d''éloquence', N'Votre argument ne convint pas le mangemort qui vous tue sur le champ', 0)
INSERT INTO [dbo].[Scenario] ([Id], [Label], [Text], [GameOver]) VALUES (35, N'So deep', N'Vos mots touche le mangemort en plein coeur il accepte de se rendre, vous avez accompli votre mission ?', 1)
INSERT INTO [dbo].[Scenario] ([Id], [Label], [Text], [GameOver]) VALUES (37, N'Nice try', N'Vous pensiez pouvoir discutter après avoir tenté de capturez quelqu''un ? Seul la mort vous attend', 2)
INSERT INTO [dbo].[Scenario] ([Id], [Label], [Text], [GameOver]) VALUES (38, N'Crache Limace', N'Dans la confusion vous lancez sur vous même le sort Crache Limace, dans votre malheur le mangemort à la phobie des limaces, et se retrouve à terre devant vous, cependant cela ne durera pas longtemps', 0)
INSERT INTO [dbo].[Scenario] ([Id], [Label], [Text], [GameOver]) VALUES (39, N'Evanoui', N'Le mangemort s''evanoui devat vous. Vous avez réussi votre mission', 1)
INSERT INTO [dbo].[Scenario] ([Id], [Label], [Text], [GameOver]) VALUES (40, N'Toothless', N'Un coup de pied bien placé lui fait perdre plusieurs de ces dents, dans l''incapacité de lancer le moindre sort vous le maitrisez facilement. Félicittions', 0)
INSERT INTO [dbo].[Scenario] ([Id], [Label], [Text], [GameOver]) VALUES (41, N'Inutile', N'Le mangemort déverouille la porte grace à un sort d''allohomora', 0)
INSERT INTO [dbo].[Scenario] ([Id], [Label], [Text], [GameOver]) VALUES (42, N'Muet', N'Le mangemort est muet pour un certain temps, cependant dans la panique vous n''arrivez pas à penser à un sort précis pour l''immobiliser', 0)
INSERT INTO [dbo].[Scenario] ([Id], [Label], [Text], [GameOver]) VALUES (43, N'Attendre ?', N'Vous attendez, le mangemort ouvre la porte et vous tue. Vous vous attendiez à quoi ?', 0)
INSERT INTO [dbo].[Scenario] ([Id], [Label], [Text], [GameOver]) VALUES (44, N'Grande Salle', N'Vous entrez dans une grande salle empli de table, vous reperrez dans un coin de la sall un portoloin', 0)
INSERT INTO [dbo].[Scenario] ([Id], [Label], [Text], [GameOver]) VALUES (45, N'Grande Sall V2', N'Vous entrez dans une grande salle empli de table, vous reperrez dans un coin de la sall un portoloin', 0)
INSERT INTO [dbo].[Scenario] ([Id], [Label], [Text], [GameOver]) VALUES (46, N'Inactif', N'Le Portoloin est malheuresement inactif.', 0)
INSERT INTO [dbo].[Scenario] ([Id], [Label], [Text], [GameOver]) VALUES (47, N'Toilettes', N'Vous vous retrouvez .... Dans des toilettes ??? Vous sentez une envie de faire caca monter en vous', 0)
INSERT INTO [dbo].[Scenario] ([Id], [Label], [Text], [GameOver]) VALUES (48, N'Geôle', N'Vous arrivez dans les geôle du donjon dedans un garde qui, en vous voyant ,se lève prend, son épée en main et vous dit de partir sous peine de tater de mon épée', 0)
INSERT INTO [dbo].[Scenario] ([Id], [Label], [Text], [GameOver]) VALUES (49, N'Plus fort que toi', N'Le sbire était en réalité un maitre de l''épée il vous tue sans grand problème.', 0)
INSERT INTO [dbo].[Scenario] ([Id], [Label], [Text], [GameOver]) VALUES (50, N'1v20', N'20 personnes arrive dans la pièce, il vous répère vous attaque et vous battent sans trop de difficulté', 2)
INSERT INTO [dbo].[Scenario] ([Id], [Label], [Text], [GameOver]) VALUES (51, N'Révélation', N'Le sbire sous l''emprise de Imperium vous donne la positiondu mangemort : la porte de début du donjon, deux choix s''offre alors à vous, allez avértir le ministère pour obtenir des renforts ou combattre le mangermort seul.', 0)
INSERT INTO [dbo].[Scenario] ([Id], [Label], [Text], [GameOver]) VALUES (52, N'Renfort Raté', N'Le mangemort à très vite découvert vos manigances et à juste changer de repère', 2)
INSERT INTO [dbo].[Scenario] ([Id], [Label], [Text], [GameOver]) VALUES (53, N'Repérage', N'Vous entrouvez la porte et appercevez une peronne dans la pièce sur laquelle donne les toilette, vous refermez discrétement la porte des toilettes.', 0)
INSERT INTO [dbo].[Scenario] ([Id], [Label], [Text], [GameOver]) VALUES (54, N'Quelqu''un arrive', N'Vous entendez une personne se rapprocher des toilletes.', 0)
INSERT INTO [dbo].[Scenario] ([Id], [Label], [Text], [GameOver]) VALUES (55, N'Porte dans ta gueule', N'En tentant de sortir vous prenez la porte en pleine tête, sonné le mangemort vous tue facilement', 2)
INSERT INTO [dbo].[Scenario] ([Id], [Label], [Text], [GameOver]) VALUES (56, N'Surprise', N'Pris par surprise vous n''avez aucun mal à maitriser le mangemort', 1)
INSERT INTO [dbo].[Scenario] ([Id], [Label], [Text], [GameOver]) VALUES (57, N'Orientation : 0/20', N'Perdu dans le donjon, vous tombez sur 3 sbires à la première intersection, emcombré par le corps du mangemort il vous térasse.', 0)
INSERT INTO [dbo].[Scenario] ([Id], [Label], [Text], [GameOver]) VALUES (58, N'Orientation : 20/20', N'Vous ouvrez la première porte sur votre chemin et par miracle sortez du donjon.', 1)
INSERT INTO [dbo].[Scenario] ([Id], [Label], [Text], [GameOver]) VALUES (59, N'Pet Bruyant', N'Le bruit d''un de vos pet alerte une personne que vous entende arriver à toute vitesse', 0)
INSERT INTO [dbo].[Scenario] ([Id], [Label], [Text], [GameOver]) VALUES (60, N'Baguette ?', N'Malheuresement ce n''est pas votre baguette que vous tenez entre vos main... Vous êtes mort (de honte et littéralement)', 2)
INSERT INTO [dbo].[Scenario] ([Id], [Label], [Text], [GameOver]) VALUES (61, N'Face à face', N'Le mangemort se tiens face à vous et demande ce que vous faites la', 0)
INSERT INTO [dbo].[Scenario] ([Id], [Label], [Text], [GameOver]) VALUES (62, N'Tout avouer', N'Bien evidemment il vous tue', 2)
INSERT INTO [dbo].[Scenario] ([Id], [Label], [Text], [GameOver]) VALUES (63, N'Eloquance', N'Etonnement il vous croit, il ouvre la porte et vous indique la sortie, la première porte en partant à gauche, il fait demi-tour et vous montre donc son dos.', 0)
